[code]
//Author: Joseph McCullough et al
//http://www.vertstudios.com/blog/less-app-windows-sorta/
//Let's the poor Windows users have some LESS fun ;)

var path = require('path'),
    fs = require('fs'),
    sys = require('sys');

//http://bit.ly/dKlPqQ (stack overflow) for package.json use
require.paths.unshift('./node_modules')

//snag the less module
var less = require('./lessjs');

//Let the user know less is listening for file changes.
sys.puts(&quot;\n&quot; + stylize(&quot;Watcher - looking for .less files...&quot;, 'underline') + &quot;\n&quot;);

//For each file in the current directory...
var lesspath = &quot;./less/&quot;;
var csspath = &quot;./css/&quot;

fs.readdirSync(lesspath).forEach(function (file) {
	//If it isn't a less file, break out.
		if (! /\.less/.test(file)) { return }

	var filepath = lesspath + file;

	//Convert less to CSS. Write to file if successful.
	toCSS(filepath, function (err, less) {
			writeCSS(file,err,less);
	});
});


//For each file in the current directory...
fs.readdirSync(lesspath).forEach(function (file) {
	//If it isn't a less file, break out.
    if (! /\.less/.test(file)) { return }

	var filepath = lesspath + file;
	
	//Add a file watch
	fs.watchFile(filepath, { persistent: true, interval: 200}, function(curr,prev)
	{
		//Convert less to CSS. Write to file if successful.
		toCSS(filepath, function (err, less) {
				writeCSS(file, err,less);
			});
	});
});

function toCSS(path, callback) {
    var tree, css;
    fs.readFile(path, 'utf-8', function (e, str) {
        if (e) { return callback(e) }

        new(less.Parser)({
            paths: [require('path').dirname(path)],
            optimization: 0
        }).parse(str, function (err, tree) {
            if (err) {
                callback(err);
            } else {
                try {
                    css = tree.toCSS({compress: true});
                    callback(null, css);
                } catch (e) {
                    callback(e);
                }
            }
        });
    });
}

function writeCSS(file,err,less){
	if (err) {sys.puts(stylize(&quot;ERROR: &quot; + (err &amp;&amp; err.message), 'red'));}
	else
	{
		var cssFile = csspath + file.replace(/less/,&quot;css&quot;);
		fs.writeFile(cssFile, less, function(err)
		{
				if (err){
					sys.puts(err);
				} else {
					sys.puts(cssFile + &quot; successfully updated.&quot;);
				}
		}); 
	}
}

// Stylize a string
function stylize(str, style) {
    var styles = {
        'bold'      : [1,  22],
        'inverse'   : [7,  27],
        'underline' : [4,  24],
        'yellow'    : [33, 39],
        'green'     : [32, 39],
        'red'       : [31, 39]
    };
    return '33[' + styles[style][0] + 'm' + str +
           '33[' + styles[style][1] + 'm';
}
[/code]
